mapper.delete:
    	delete from toa_archive_document
      		where ID in
      		
	            #{primaryKey}

mapper.update:
    	UPDATE toa_archive_document t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in
      		
	            #{primaryKey}

    	UPDATE toa_archive_document t set t.DM_IN_RECYCLE = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in
      		
	            #{primaryKey}

mapper.select:
