		WHERE t.ID !=#{id} and  t.ID not in (select r.DM_RELATE_ID from toa_archive_relate r where r.DELETE_FLAG = 0 and r.DOCUMENT_ID = #{id}) and (select f_find_parent_folder(t.FOLDER_ID) != -1)
				
		
			and t.BACK_UP_ID = #{backUpId}
		
		
		
