mapper.select:
		SELECT
		t.ID id,
		t.USER_ID userId,
		t.LOGIN_NAME loginName,
		t.DISPLAY_NAME displayName,
		t.IP ip,
		t.login_device loginDevice,
		t.LOGIN_TIME loginTime,
		t.LOGOUT_TIME logoutTime,
		t.DELETE_FLAG deleteFlag,
		t.CREATE_USER createUser,
		t.CREATE_USER_DEPT createUserDept,
		t.CREATE_USER_ORG createUserOrg,
		t.CREATE_DATE createDate,
		t.MODIFY_USER modifyUser,
		t.MODIFY_DATE modifyDate

		FROM tty_sys_loginlog t
		
			
				and t.ID = #{id}
			
			
				and t.USER_ID = #{userId}
			
			
				and t.LOGIN_NAME like '%${loginName}%'
			
			
				and t.DISPLAY_NAME like '%${displayName}%'
			
			
				and t.IP = #{ip}
			
			
				
					and t.login_device = #{loginDevice}
				
			
			
				and t.DELETE_FLAG = #{deleteFlag}
			
			
				and t.CREATE_USER = #{createUser}
			
			
				and t.CREATE_USER_DEPT = #{createUserDept}
			
			
				and t.CREATE_DATE = #{createDate}
			
			
				and t.MODIFY_USER = #{modifyUser}
			
			
				and t.MODIFY_DATE = #{modifyDate}
			
			
				
					and t.LOGIN_TIME != ""
					
						and t.LOGIN_TIME >= #{loginTimeBegin}
					
					
						and t.LOGIN_TIME <= #{loginTimeEnd}
					
				
				
					and t.LOGOUT_TIME != ""
					
						and t.LOGOUT_TIME >= #{logoutTimeBegin}
					
					
						and t.LOGOUT_TIME <= #{logoutTimeEnd}
					
				
				
					
						
							and ((t.LOGIN_TIME >= #{loginTimeBegin} and t.LOGIN_TIME <= #{loginTimeEnd}) or
							(t.LOGOUT_TIME >= #{logoutTimeBegin} and t.LOGOUT_TIME <= #{logoutTimeEnd}))
						
					
					
						
							and (t.LOGIN_TIME >= #{loginTimeBegin} or t.LOGOUT_TIME >= #{logoutTimeBegin})
						
					
					
						
							and (t.LOGIN_TIME <= #{loginTimeEnd} or t.LOGOUT_TIME <= #{logoutTimeEnd})
						
					
				
			
			
				order by ${orderBy}

SELECT count(ID)
FROM tty_sys_loginlog t

	
		${dataAccessDynamicSQL}
	
	
		and t.ID = #{id}
	
	
		and t.USER_ID = #{userId}
	
	
		and t.LOGIN_NAME like '%${loginName}%'
	
	
		and t.DISPLAY_NAME like '%${displayName}%'
	
	
		and t.IP = #{ip}
	
	
		
			and t.login_device = #{loginDevice}
		
	
	
		and t.DELETE_FLAG = #{deleteFlag}
	
	
		and t.CREATE_USER = #{createUser}
	
	
		and t.CREATE_USER_DEPT = #{createUserDept}
	
	
		and t.CREATE_DATE = #{createDate}
	
	
		and t.MODIFY_USER = #{modifyUser}
	
	
		and t.MODIFY_DATE = #{modifyDate}
	
	
		
			and t.LOGIN_TIME != ""
			
				and t.LOGIN_TIME >= #{loginTimeBegin}
			
			
				and t.LOGIN_TIME <= #{loginTimeEnd}
			
		
		
			and t.LOGOUT_TIME != ""
			
				and t.LOGOUT_TIME >= #{logoutTimeBegin}
			
			
				and t.LOGOUT_TIME <= #{logoutTimeEnd}
			
		
		
			
				
					and ((t.LOGIN_TIME >= #{loginTimeBegin} and t.LOGIN_TIME <= #{loginTimeEnd}) or
					(t.LOGOUT_TIME >= #{logoutTimeBegin} and t.LOGOUT_TIME <= #{logoutTimeEnd}))
				
			
			
				
					and (t.LOGIN_TIME >= #{loginTimeBegin} or t.LOGOUT_TIME >= #{logoutTimeBegin})
				
			
			
				
					and (t.LOGIN_TIME <= #{loginTimeEnd} or t.LOGOUT_TIME <= #{logoutTimeEnd})

