mapper.update:
UPDATE tty_sys_menu t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
where ID in

	#{primaryKey}

mapper.select:
SELECT t.ID id, t.NAME name, t.WEIGHT weight, t.PARENT_ID parentId, t.ICON icon, t.MENU_TYPE menuType, t.ACTION_NAME actionName, t.DELETE_FLAG deleteFlag, t.QUEUE queue, t.CREATE_USER createUser, t.CREATE_USER_DEPT createUserDept, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.IS_SHOW isShow, t.permission permission FROM tty_sys_menu t,tty_sys_role_menu r, tty_sys_user_role u
t.ID = r.MENU_ID and r.ROLE_ID = u.ROLE_ID
and t.ID = #{id}
and u.USER_ID = #{userId}
and t.WEIGHT <= #{weight}
