SELECT t.ID as id , t.NAME as name , t.WEIGHT weight, t.WEIGHT_BESTRIDE as bestrideWeight, t.dept_id as deptId, t1.name as deptName, t1.weight as deptWeight, t.DESCRIPTION as description , t.DELETE_FLAG as deleteFlag , t.CREATE_USER as createUser , t.CREATE_DATE as createDate, t.CREATE_USER_DEPT createUserDept, t.CREATE_USER_ORG createUserOrg, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.REMARK1 remark1, COUNT(t2.ROLE_ID) personNum FROM tty_sys_role t left join tty_sys_department t1 on t.dept_id = t1.id AND t1.DELETE_FLAG = 0 LEFT JOIN tty_sys_user_role t2 ON t.ID = t2.ROLE_ID AND t2.DELETE_FLAG = 0 WHERE 1 = 1
and t.ID = #{id}
and t.NAME = #{name}
and t.WEIGHT <= #{weight}
and t.dept_id in
'${item}'
and t.DESCRIPTION = #{description}
and t.DELETE_FLAG = #{deleteFlag}
and t.CREATE_USER = #{createUser}
and t.CREATE_DATE = #{createDate}
and t1.CODE like (select concat(son.code, '%') from tty_sys_department son where son.id = #{deptIdToCode})
GROUP BY id,deptId,description,createUser,createUserDept,modifyDate
order by ${orderBy}

