mapper.select:
		SELECT
				t.ID as id ,
				t.NAME as name ,
				t.WEIGHT as weight,
				t.WEIGHT_BESTRIDE as bestrideWeight,
				t.dept_id as deptId
				
		FROM 
				tty_sys_role t  WHERE t.DELETE_FLAG = 0
				
			
				and t.ID = #{id}
			
			
				and t.NAME = #{name}
			
			
				and t.WEIGHT <= #{weight}
			
			
				and t.dept_id in
				
					'${item}'
				
			

			
				and t.CREATE_USER = #{createUser}
			
			
				and t.CREATE_DATE = #{createDate}
			
			
			
				order by ${orderBy}

		SELECT
		t.ID as id ,
		t.NAME as name ,
		t.WEIGHT as weight,
		t.WEIGHT_BESTRIDE as bestrideWeight,
		t.dept_id as deptId

		FROM
		tty_sys_role t
		WHERE
		1=1
		
			and t.DELETE_FLAG = #{deleteFlag}
		
		
			and t.ID = #{id}
		
		
			and t.NAME = #{name}
		
		
			and t.WEIGHT <= #{weight}
		
		
			and t.dept_id in
			
				'${item}'
			
		

		
			and t.CREATE_USER = #{createUser}
		
		
