SELECT t.ID as id , t.NAME as name, t.WEIGHT as weight, t.WEIGHT_BESTRIDE as bestrideWeight, t1.name as deptName FROM tty_sys_role t,tty_sys_department t1
t.dept_Id = t1.id
and t.ID = #{id}
and t.WEIGHT <= #{weight}
and t.name like '%${name}%'
and t1.name like '%${deptName}%'

SELECT t.ID as id , t.NAME as name, t.WEIGHT as weight, t.WEIGHT_BESTRIDE as bestrideWeight, e.PERMISSION_TYPE as weightType FROM tty_sys_role t,tty_sys_user_role u, tty_sys_role_menu m, tty_sys_role_ext e
t.ID = u.ROLE_ID AND t.ID = m.ROLE_ID AND t.ID = e.ROLE_ID
and u.USER_ID = #{userId}
and m.MENU_ID = #{menuIds}
