mapper.select:
SELECT 
	t.USER_ID userId,
	t1.DISPLAY_NAME userName,
	t.ROLE_ID roleId,
	t.DEPT_ID deptId,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5
	
FROM tty_sys_user_role t,tty_sys_user t1

	and t.USER_ID = t1.ID
	
		and t.USER_ID = #{userId}
	
	
		and t.ROLE_ID = #{roleId}
	
	
		and t.DEPT_ID = #{deptId}
	
	
		and t.DELETE_FLAG = #{deleteFlag}
	
	
		and t.CREATE_USER = #{createUser}
	
	
		and t.CREATE_USER_DEPT = #{createUserDept}
	
	
		and t.CREATE_DATE = #{createDate}
	
	
		and t.MODIFY_USER = #{modifyUser}
	
	
		and t.MODIFY_DATE = #{modifyDate}
	
	
		and t.EXT_STR1 = #{extStr1}
	
	
		and t.EXT_STR2 = #{extStr2}
	
	
		and t.EXT_STR3 = #{extStr3}
	
	
		and t.EXT_STR4 = #{extStr4}
	
	
		and t.EXT_STR5 = #{extStr5}

SELECT count(USER_ID) 
FROM tty_sys_user_role t

	
		and t.USER_ID = #{userId}
	
	
		and t.ROLE_ID = #{roleId}
	
	
		and t.DEPT_ID = #{deptId}
	
	
		and t.DELETE_FLAG = #{deleteFlag}
	
	
		and t.CREATE_USER = #{createUser}
	
	
		and t.CREATE_USER_DEPT = #{createUserDept}
	
	
		and t.CREATE_DATE = #{createDate}
	
	
		and t.MODIFY_USER = #{modifyUser}
	
	
		and t.MODIFY_DATE = #{modifyDate}
	
	
		and t.EXT_STR1 = #{extStr1}
	
	
		and t.EXT_STR2 = #{extStr2}
	
	
		and t.EXT_STR3 = #{extStr3}
	
	
		and t.EXT_STR4 = #{extStr4}
	
	
		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from tty_sys_user_role
      		where USER_ID in
      		
	            #{primaryKey}

delete from tty_sys_user_role where
ROLE_ID in
#{roleId}

mapper.update:
    	UPDATE tty_sys_user_role t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where USER_ID in
      		
	            #{primaryKey}
