mapper.select:
		SELECT
		t.ID id,
		t.USER_ID userId,
		t.USER_NAME userName,
		t.USER_INITIALS userInitials,
		t.USER_ABBREVIATE userAbbreviate,
		t.USER_FULL userFull,
		t.DELETE_FLAG deleteFlag,
		t.CREATE_USER createUser,
		t.CREATE_USER_DEPT createUserDept,
		t.CREATE_USER_ORG createUserOrg,
		t.CREATE_DATE createDate,
		t.MODIFY_USER modifyUser,
		t.MODIFY_DATE modifyDate,
		t.EXT_STR1 extStr1,
		t.EXT_STR2 extStr2,
		t.EXT_STR3 extStr3,
		t.EXT_STR4 extStr4,
		t.EXT_STR5 extStr5,
		t.EXT_DATE1 extDate1,
		t.EXT_DATE2 extDate2,
		t.ext_num1 extNum1,
		t.ext_num2 extNum2,
		t.ext_num3 extNum3

		FROM tty_sys_pin_user t
		



			
				and t.ID = #{id}
			
			
				and t.USER_ID = #{userId}
			
			
				and t.USER_NAME like '%${userName}%'
			
			
				and t.USER_INITIALS = #{userInitials}
			
			
				and t.USER_ABBREVIATE = #{userAbbreviate}
			
			
				and t.USER_FULL = #{userFull}
			
			
				and t.DELETE_FLAG = #{deleteFlag}
			
			
				and t.CREATE_USER = #{createUser}
			
			
				and t.CREATE_USER_DEPT = #{createUserDept}
			
			
				and t.CREATE_USER_ORG = #{createUserOrg}
			
			
				and t.CREATE_DATE = #{createDate}
			
			
				and t.CREATE_DATE >= #{createDateBegin}
			
			
				and t.CREATE_DATE <= #{createDateEnd}
			
			
				and t.MODIFY_USER = #{modifyUser}
			
			
				and t.MODIFY_DATE = #{modifyDate}
			
			
				and t.MODIFY_DATE >= #{modifyDateBegin}
			
			
				and t.MODIFY_DATE <= #{modifyDateEnd}
			
			
				and t.EXT_STR1 = #{extStr1}
			
			
				and t.EXT_STR2 = #{extStr2}
			
			
				and t.EXT_STR3 = #{extStr3}
			
			
				and t.EXT_STR4 = #{extStr4}
			
			
				and t.EXT_STR5 = #{extStr5}
			
			
				and t.EXT_DATE1 = #{extDate1}
			
			
				and t.EXT_DATE1 >= #{extDate1Begin}
			
			
				and t.EXT_DATE1 <= #{extDate1End}
			
			
				and t.EXT_DATE2 = #{extDate2}
			
			
				and t.EXT_DATE2 >= #{extDate2Begin}
			
			
				and t.EXT_DATE2 <= #{extDate2End}
			
			
				and t.ext_num1 = #{extNum1}
			
			
				and t.ext_num2 = #{extNum2}
			
			
				and t.ext_num3 = #{extNum3}
			
		
		
			order by ${orderBy}

		SELECT count(t.ID)
		FROM tty_sys_pin_user t
		



			
				and t.ID = #{id}
			
			
				and t.USER_ID = #{userId}
			
			
				and t.USER_NAME like '%${userName}%'
			
			
				and t.USER_INITIALS = #{userInitials}
			
			
				and t.USER_ABBREVIATE = #{userAbbreviate}
			
			
				and t.USER_FULL = #{userFull}
			
			
				and t.DELETE_FLAG = #{deleteFlag}
			
			
				and t.CREATE_USER = #{createUser}
			
			
				and t.CREATE_USER_DEPT = #{createUserDept}
			
			
				and t.CREATE_USER_ORG = #{createUserOrg}
			
			
				and t.CREATE_DATE = #{createDate}
			
			
				and t.CREATE_DATE >= #{createDateBegin}
			
			
				and t.CREATE_DATE <= #{createDateEnd}
			
			
				and t.MODIFY_USER = #{modifyUser}
			
			
				and t.MODIFY_DATE = #{modifyDate}
			
			
				and t.MODIFY_DATE >= #{modifyDateBegin}
			
			
				and t.MODIFY_DATE <= #{modifyDateEnd}
			
			
				and t.EXT_STR1 = #{extStr1}
			
			
				and t.EXT_STR2 = #{extStr2}
			
			
				and t.EXT_STR3 = #{extStr3}
			
			
				and t.EXT_STR4 = #{extStr4}
			
			
				and t.EXT_STR5 = #{extStr5}
			
			
				and t.EXT_DATE1 = #{extDate1}
			
			
				and t.EXT_DATE1 >= #{extDate1Begin}
			
			
				and t.EXT_DATE1 <= #{extDate1End}
			
			
				and t.EXT_DATE2 = #{extDate2}
			
			
				and t.EXT_DATE2 >= #{extDate2Begin}
			
			
				and t.EXT_DATE2 <= #{extDate2End}
			
			
				and t.ext_num1 = #{extNum1}
			
			
				and t.ext_num2 = #{extNum2}
			
			
				and t.ext_num3 = #{extNum3}

mapper.delete:
delete from tty_sys_pin_user
where ID in

	#{primaryKey}

mapper.update:
UPDATE tty_sys_pin_user t set t.delete_flag = 1
where t.USER_ID in

	#{primaryKey}

UPDATE tty_sys_pin_user t set t.delete_flag = 0
where t.USER_ID in

	#{primaryKey}

mapper.select:
SELECT u.ID Id, u.ID userId, u.DISPLAY_NAME userName, u.DISPLAY_NAME text, t.USER_INITIALS userInitials, t.USER_ABBREVIATE userAbbreviate, t.USER_FULL userFull, u.DEPT_ID deptId, u.DUTY_ID dutyId, u.ORDER_NO orderNo, u.IS_ADMIN weightRole
FROM tty_sys_pin_user t right join tty_sys_user u on t.USER_ID = u.id
t.DELETE_FLAG = 0
and t.USER_ID = #{userId}
and t.USER_INITIALS like '%${userInitials}%'
and (t.USER_INITIALS like '%${userInitials}%' or t.USER_NAME like '%${userName}%' or t.USER_ABBREVIATE like '%${userAbbreviate}%' or t.USER_FULL like '%${userFull}%')
and t.USER_INITIALS like '%${userInitials}%' and (t.USER_NAME like '%${userName}%' or t.USER_ABBREVIATE like '%${userAbbreviate}%' or t.USER_FULL like '%${userFull}%')
and u.DUTY_ID = #{dutyId}
and t.EXT_STR1 = #{extStr1}
order by u.ORDER_NO
