mapper.select:
		SELECT 
			t.ID id,
			t.SYS_PERMISSION sysPermission,
			t.TAB_TITLE tabTitle,
			t.TAB_URL tabUrl,
			t.TAB_FLAG tabFlag,
			t.DELETE_FLAG deleteFlag,
			t.CREATE_USER createUser,
			t.CREATE_USER_DEPT createUserDept,
			t.CREATE_USER_ORG createUserOrg,
			t.CREATE_DATE createDate,
			t.MODIFY_USER modifyUser,
			t.MODIFY_DATE modifyDate,
			t.EXT_STR1 extStr1,
			t.EXT_STR2 extStr2,
			t.EXT_STR3 extStr3,
			t.EXT_STR4 extStr4,
			t.EXT_STR5 extStr5,
			t.EXT_DATE1 extDate1,
			t.EXT_DATE2 extDate2,
			t.ext_num1 extNum1,
			t.ext_num2 extNum2,
			t.ext_num3 extNum3
			
		FROM tty_sys_tab_tree t
		
		
		

			
				and t.ID = #{id}
			
			
				and t.SYS_PERMISSION = #{sysPermission}
			
			
				and t.TAB_TITLE = #{tabTitle}
			
			
				and t.TAB_URL = #{tabUrl}
			
			
				and t.TAB_FLAG = #{tabFlag}
			
			
				and t.DELETE_FLAG = #{deleteFlag}
			
			
				and t.CREATE_USER = #{createUser}
			
			
				and t.CREATE_USER_DEPT = #{createUserDept}
			
			
				and t.CREATE_USER_ORG = #{createUserOrg}
			
			
				and t.CREATE_DATE = #{createDate}
			
			
				and t.CREATE_DATE >= #{createDateBegin}
			
			
				and t.CREATE_DATE <= #{createDateEnd}
			
			
				and t.MODIFY_USER = #{modifyUser}
			
			
				and t.MODIFY_DATE = #{modifyDate}
			
			
				and t.MODIFY_DATE >= #{modifyDateBegin}
			
			
				and t.MODIFY_DATE <= #{modifyDateEnd}
			
			
				and t.EXT_STR1 = #{extStr1}
			
			
				and t.EXT_STR2 = #{extStr2}
			
			
				and t.EXT_STR3 = #{extStr3}
			
			
				and t.EXT_STR4 = #{extStr4}
			
			
				and t.EXT_STR5 = #{extStr5}
			
			
				and t.EXT_DATE1 = #{extDate1}
			
			
				and t.EXT_DATE1 >= #{extDate1Begin}
			
			
				and t.EXT_DATE1 <= #{extDate1End}
			
			
				and t.EXT_DATE2 = #{extDate2}
			
			
				and t.EXT_DATE2 >= #{extDate2Begin}
			
			
				and t.EXT_DATE2 <= #{extDate2End}
			
			
				and t.ext_num1 = #{extNum1}
			
			
				and t.ext_num2 = #{extNum2}
			
			
				and t.ext_num3 = #{extNum3}
			
		
		
			order by ${orderBy}

		SELECT count(t.ID) 
		FROM tty_sys_tab_tree t
		
		
		

			
				and t.ID = #{id}
			
			
				and t.SYS_PERMISSION = #{sysPermission}
			
			
				and t.TAB_TITLE = #{tabTitle}
			
			
				and t.TAB_URL = #{tabUrl}
			
			
				and t.TAB_FLAG = #{tabFlag}
			
			
				and t.DELETE_FLAG = #{deleteFlag}
			
			
				and t.CREATE_USER = #{createUser}
			
			
				and t.CREATE_USER_DEPT = #{createUserDept}
			
			
				and t.CREATE_USER_ORG = #{createUserOrg}
			
			
				and t.CREATE_DATE = #{createDate}
			
			
				and t.CREATE_DATE >= #{createDateBegin}
			
			
				and t.CREATE_DATE <= #{createDateEnd}
			
			
				and t.MODIFY_USER = #{modifyUser}
			
			
				and t.MODIFY_DATE = #{modifyDate}
			
			
				and t.MODIFY_DATE >= #{modifyDateBegin}
			
			
				and t.MODIFY_DATE <= #{modifyDateEnd}
			
			
				and t.EXT_STR1 = #{extStr1}
			
			
				and t.EXT_STR2 = #{extStr2}
			
			
				and t.EXT_STR3 = #{extStr3}
			
			
				and t.EXT_STR4 = #{extStr4}
			
			
				and t.EXT_STR5 = #{extStr5}
			
			
				and t.EXT_DATE1 = #{extDate1}
			
			
				and t.EXT_DATE1 >= #{extDate1Begin}
			
			
				and t.EXT_DATE1 <= #{extDate1End}
			
			
				and t.EXT_DATE2 = #{extDate2}
			
			
				and t.EXT_DATE2 >= #{extDate2Begin}
			
			
				and t.EXT_DATE2 <= #{extDate2End}
			
			
				and t.ext_num1 = #{extNum1}
			
			
				and t.ext_num2 = #{extNum2}
			
			
				and t.ext_num3 = #{extNum3}

mapper.delete:
    	delete from tty_sys_tab_tree
      		where ID in
      		
	            #{primaryKey}

mapper.update:
    	UPDATE tty_sys_tab_tree t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in
      		
	            #{primaryKey}
